mapper.select:
SELECT id, user_id, title, status, created_at
FROM todos
WHERE user_id = #{userId}
ORDER BY created_at DESC
LIMIT #{size} OFFSET #{offset}

SELECT id, user_id, title, status, created_at
FROM todos
WHERE id = #{id}
  AND user_id = #{userId}

mapper.update:
UPDATE todos


        title = #{title},


        status = #{status},


WHERE id = #{id}
AND user_id = #{userId}

mapper.delete:
DELETE
FROM todos
WHERE id = #{id}
  AND user_id = #{userId}

mapper.select:
SELECT COUNT(*)
FROM todos
WHERE user_id = #{userId}
